import React from "react";
import Chart from "../Chart/Chart";
import Card from "../UI/Card";

const ExpenseChart = (props) => {
  let sumOfExpenses = 0;

  const months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  props.expenses.forEach((expense) => {
    sumOfExpenses += expense.amount;
  });
  console.log(sumOfExpenses);
  const showMonthlyVal = props.month == -1 ? false : true;

  return (
    <Card className="expense-item">
      <table width={"100%"}>
        <tr>
          <td width={"15%"}>
            <div>
              <Chart value={sumOfExpenses} />
            </div>
          </td>
          <td style={{ color: "white", width: "60%" }}>
            <table width={"100%"}>
              <tr>
                <td align="center">Showing Expenses for</td>
                <td align="center">Total Budget</td> <td>Total Expenditure</td>
                <td align="center">Available Budget</td>
              </tr>
              <tr>
                <td align="center">
                    {showMonthlyVal && <h2>{months[props.month]}</h2>}
                    {!showMonthlyVal && <h2>{props.year}</h2>}
                </td>
                <td align="center">
                  <h2>1000</h2>
                </td>
                <td align="center"><h2>{sumOfExpenses}</h2></td>
                <td align="center">{(1000-sumOfExpenses).toString()}</td>
              </tr>
            </table>
          </td>
        </tr>
      </table>
    </Card>
  );
};

export default ExpenseChart;
