import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";
import Chart from "../Chart/Chart";
function Expenses(props) {
  const [filterYear, setFilterYear] = useState("2023");
  const [filterMonth, setFilterMonth] = useState(-1);
  const expenses = props.items;
  const handleFilterYear = (year) => {
    console.log(year);
    setFilterYear(year);
  };
  const handleFilterMonth = (month) => {
    console.log(month);
    setFilterMonth(month);
  };

  let filteredExpenses = expenses.filter(
    (x) => x.date.getFullYear().toString() === filterYear
  );

  if(filterMonth>=0){
    filteredExpenses = expenses.filter(
      (x) => x.date.getMonth().toString() === filterMonth
    );
  }
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          year={filterYear}
          month={filterMonth}
          onChangeFilterYear={handleFilterYear}
          onChangeFilterMonth={handleFilterMonth}
        />
        <Chart exp={filteredExpenses} />
        {filteredExpenses.length === 0 ? (
          <p className="expenses-list__notFound">No Expenses Found</p>
        ) : (
          filteredExpenses.map((exp) => (
            <ExpenseItem
              key={exp.id}
              date={exp.date}
              title={exp.title}
              amount={exp.amount}
            />
          ))
        )}
        {/* <ExpenseItem
          date={expenses[0].date}
          title={expenses[0].title}
          amount={expenses[0].amount}
        />
        <ExpenseItem
          date={expenses[1].date}
          title={expenses[1].title}
          amount={expenses[1].amount}
        />
        <ExpenseItem
          date={expenses[2].date}
          title={expenses[2].title}
          amount={expenses[2].amount}
        />
        <ExpenseItem
          date={expenses[3].date}
          title={expenses[3].title}
          amount={expenses[3].amount}
        /> */}
      </Card>
    </div>
  );
}
export default Expenses;
