import React from "react";
import "./Chart.css";
import ChartBar from "./ChartBar";

const Chart = (props) => {
  const expenses = props.exp;
  const monthNames = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];
  return (
    <div className="chart">
      {expenses.map((m) => (
        <ChartBar
          key={m.label}
          value={m.amount}
          label={m.title}
          maxVal={null}
        />
      ))}
    </div>
  );
};

export default Chart;
