import React from "react";
import Chart from "../Chart/Chart";
import Card from "../UI/Card";

const ExpenseChart = (props) => {
  let sumOfExpenses = 0;

  props.expenses.forEach((expense) => {
    sumOfExpenses += expense.amount;
  });
  console.log(sumOfExpenses);

  return (
    <Card className="expense-item">
      <table width={"100%"}>
        <tr>
          <td width={"20%"}>
            <div>
              <Chart value={sumOfExpenses} />
            </div>
          </td>
          <td style={{ color: "white", width: "60%" }}>
            <table>
              <tr>
                <td>Showing Expenses for:</td>
                <td>Total Expenditure:</td> <td>Total Budget:</td>
                <td>Available Budget:</td>
              </tr>
              <tr>
                <td>March</td>
                <td>2000</td> <td>2120</td>
                <td>120</td>
              </tr>
              {/* <tr>
                    <td>Showing Expenses for:</td>
                </tr>
                <tr>
                    <td>Total Expenditure:</td>
                </tr>
                <tr>
                    <td>Total Budget:</td>
                </tr>
                <tr>
                    <td>Available Budget:</td>
                </tr> */}
            </table>
          </td>
        </tr>
      </table>
    </Card>
  );
};

export default ExpenseChart;
