import { useEffect, useState } from "react";
import Expenses from "./Components/Expenses/Expenses";
import NewExpense from "./Components/NewExpenses/NewExpense";
import { EasybaseProvider, useEasybase } from "easybase-react";

const initExpenses = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94,
    date: new Date(2023, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799, date: new Date(2023, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294,
    date: new Date(2023, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2023, 5, 12),
  },
];
function App() {
  const [expensesBe, setExpensesBe] = useState();
  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setExpensesBe(data);
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, []);

  const [expenses, setExpenses] = useState(expensesBe);

  const addExpenseHandler = (enteredExpense) => {
    console.log("From app.js ");
    console.log(enteredExpense);
    setExpenses((previousExpenses) => [enteredExpense, ...previousExpenses]);
    console.log(expenses);
  };

  return (
    <div>
      <EasybaseProvider>
        <NewExpense onAddExpense={addExpenseHandler} />
        <div>
          <Expenses items={expenses} />
        </div>
      </EasybaseProvider>
    </div>
  );
}

export default App;
