import "./ExpenseItem.css";
import ExpenseDate from "./ExpenseDate";
import Card from "../UI/Card";



function ExpenseItem(props) {

  const DeleteHandler = () => {
    const data = {
      EditDelete: 'D',
      txnId: props.id
    }
    console.log(data);
    props.onEdit(data);
  }
  const EditHandler = () => {
    const data = {
      EditDelete: 'E',
      txnId: props.id,
      title: props.title,
      amount: props.amount
    }
    console.log(data);
    props.onEdit(data);
  }
  const expenseDate = props.date;
  const expenseTitle = props.title;
  const amount = props.amount;

  return (
    <Card className="expense-item">
      <ExpenseDate date={expenseDate}></ExpenseDate>

      <div className="expense-item__description">
        <h2>{expenseTitle}</h2>
        <p className="expense-item__price">INR {amount}</p>
        <button onClick={DeleteHandler}><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="red" class="bi bi-trash" viewBox="0 0 16 16">
  <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5Zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5Zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6Z"/>
  <path d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1ZM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118ZM2.5 3h11V2h-11v1Z"/>
</svg></button>
      </div>

    </Card>
  );
}

export default ExpenseItem;
