import "./ExpenseItem.css";
import ExpenseDate from "./ExpenseDate";
import Card from "../UI/Card";
import { useState } from "react";

function ExpenseItem(props) {

  const [expenseTitle, setTitle] = useState(props.title);
  const expenseDate = props.date;
  // let expenseTitle = props.title;
  const amount = props.amount;

  const clickHandler = () => {
    console.log("Previous val= "+ expenseTitle)
    setTitle("Updated Title")
    console.log("New val= "+ expenseTitle)
  }

  return (
    <Card className="expense-item">
      <ExpenseDate date={expenseDate}></ExpenseDate>

      <div className="expense-item__description">
        <h2>{expenseTitle}</h2>
        <p className="expense-item__price">INR {amount}</p>
      </div>
      <button onClick={clickHandler}>Change title</button>
    </Card>
  );
}

export default ExpenseItem;
